//              NAV-BAR WITH DYNAMIC LINKS                  \\

// Importing Requried Modules :
import React from 'react';
import {Link , NavLink} from 'react-router-dom'

// Link OR NavLink :
    // In React Route we use Link OR NavLink insted of Ancer tag , 
        // In case of Link the page remains Reload and having no dynamic rendering , So use NavLink it is able to do Dynamic Rendering . 

const Nav = () => {
    return (
        <>
            <div id="navBar">
                <h1>Jarviz Corporation</h1>
                    {/* exect active_class is used for CSS , you can explore it on your on.  */}
                <div className = "man">
                    <ul>
                    <li> <NavLink style={{ textDecoration: 'none' , fontSize: '20px' , color: 'black' }} exact activeClassName = "active_class" to = "/" > Home </NavLink> </li>
                    <li> <NavLink style={{ textDecoration: 'none' , fontSize: '20px' , color: 'black' }} exact activeClassName = "active_class" to = "/Services" > Services </NavLink> </li>
                    <li> <NavLink style={{ textDecoration: 'none' , fontSize: '20px' , color: 'black' }} exact activeClassName = "active_class" to = "/About" > About </NavLink> </li>
                    <li> <NavLink style={{ textDecoration: 'none' , fontSize: '20px' , color: 'black' }} exact activeClassName = "active_class" to = "/Contact" > Contact-US </NavLink> </li>
                    </ul>
                </div>
            </div>

        </>
    )
}

