const Form = () => {
  const [FullName, useFullName] = useState({
    fName: "",
    lName: "",
    Email: "",
  });

  const InputEvent = (event) => {
    console.log(event.target.value);

    const value = event.target.value;
    const name = event.target.name;

    useFullName((pValue) => {
      return (
        {
          ...pValue,
          [name]: value
        }
      )
    });
  };

