import React from'react';

// Importing Context API :
import {FirstName} from './App';

const CompB =()=>{
    return(
        <>
        <FirstName.Consumer>
            {
            (data)=>{
                <> The Value that is sent from the Main Component with the Help of Context API without Hooks is = {data} </>
            }
            }
        </FirstName.Consumer>
            This is the Component B
        </>
    )
}

export default CompB;
