// Importing Requried Modules :
import React from 'react';
import { useState } from 'react';

import './index.css'



const App = () => {
    const state = useState();

    const [oldnumber , update] = useState(0);


    const incri = ()=> {
        update(oldnumber + 1);
        console.log(oldnumber)
    }
    
    // setInterval(incri , 1000)
    return (
        <>
            <h1>Click the Button To Incriment The Number :</h1>
            <h3> {oldnumber} </h3>
            <button> Click TO Incriment. </button>
        </>
    )
};

export default App;
