//                  CERATING NUMBER INCREMENTED APP IN DOM                  \\

// Importing Requried Modules :
import React from 'react';

import './index.css'

let number = 0;

function Incri() {
    let update = number++;
    console.log(number);
    return(
        <>
            {update}
        </>
    )
}

const App = ()=>{
    return (
        <>
            <h1>Click the Button To Incriment The Number :</h1>
            <h3> {number} </h3>
            <button onClick={Incri}> Click TO Incriment. </button>
        </>
    )
};

export default App;
