//                  SERVICES PAGE AS A COMPONENT                   \\

import Home from "./Home"

// Importing Modules :
import React, { useEffect, useState } from 'react';

const Services = (props)=>{



    const [num , updateNum] = useState(0);
    const [num1 , updateNum1] = useState(50);

    const Add = ()=>{
        updateNum(num + 1)
    }
    const min = ()=>{
        updateNum1(num1 - 1)
    }
    console.log(num)

    var i = 0;

    useEffect(
        ()=>{
            for(i;  i<500;  i++){
                updateNum(num + 1)
            }
        } , [num1]
    )


    return(
        <>
            This is SERVICES Page {num} . {num1}
            <button id="sbtn" onClick={Add}> Click ME </button>
            <button id="sbtn" onClick={min}> Click ME </button>
        </>
    )
}

// Exporting Services Component :
export default Services;
