import React from 'react';
import { useState } from 'react';


const Keep = ()=>{
    const [note , updateNote] = useState("")

    const enteringNote = (data)=>{
        updateNote(data.target.value)
    }
    return (
        <>
            <div id="mainDiv">
                <input
                    type="text"
                    placeholder="Title"
                    onChange={enteringNote}
                 />
                 <textarea
                    column=""
                    rows=""
                    placeholder="Write a Note"
                 />

            </div>
            <h1> This Keeper App {note} </h1>
        </>
    )
}


export default Keep;
