//                  CERATING LIVE TIME APP WITH USE-STATE HOOK IN DOM                  \\


// Importing Requried Modules :
import React from 'react';
// Importing Hooks's Hodule (UseState) :
// This Module is Automatic Imported when you use Hook(UseState) in the Project :
import { useState } from 'react';

// Imorting CSS :
import './index.css'


// Creating Date & Time :
var date = new Date();
var curdate = date.toLocaleDateString();
var time = date.toLocaleTimeString();
var hours = date.getHours();
var greet = "";
var greetcss = {};


// Making Greet Conditions :
if (hours >= 4 && hours < 12) {
    greet = "Good Morning";
    greetcss.color = "green"
}
else if (hours >= 12 && hours < 18) {
    greet = "Good AfterNoon";
    greetcss.color = "brown"
}
else {
    greet = "Good Night";
    greetcss.color = "gray"
}

// Creating the App Component :
const App = () => {
    // As the Hooks is used in the Main Function & at the Top Level , So we started code Hook from here :
    // 'oldnumber' is the Actuall Value for the State , 'update' is the State Updater Function for Said State :
    const [oldnumber, update] = useState(0);
    // Changing the Value in the 'update' a State Updater , If the Value ion the State Updater is Changed then the State (oldnumber) is also Update.
    const incriment = () => {
        // Incrimenting the Number in the StateUpdater by 1 :
        update(oldnumber + 1);
    };

    // Rendring :
    return (
        <>
            <h2 className="greetHeading">Hellow , Sir <span style={greetcss}> {" " + greet} </span></h2>
            <h3 className="dt">Date =  <span style={{ color: "darkmagenta" }}> {" " + curdate} </span></h3>
            <h3 className="dt">Time =  <span style={{ color: "darkmagenta" }}> {" " + time} </span></h3>
        </>
    )
};


// Exporting App Component :
export default App;
