//                  CREATING FORM WITH 'FORM' TAG JUST AS SIMPLE HTML FORM             //
/*
        FORM TAG :
                The Defualt behavipur of the Form Submittion is that , it can refresh the Page & also Submit the Data ,
                 but what If we want to Submit the Data without Refreshing the page,
                  So for this Purpose :
                  * Use the Input tags withOut 'form' Tag (As we already discuss this method in Form1 Example)
                  * Another way is achieved by controlling the Submittion Function AS :
                                '<form  onSubmit={xyz} > </form>' , 'onSubmit' Attribute Gives us power to control the Submission behaviour of Forms.
                                    TO Stop refresh us 'x.preventDefault()' function , As the 'x' is the Parameter's Object that is Given by the 'onSubmit' function.

*/

import React from 'react';
import { useState } from 'react';



// Creating Form Component :
const Form = () => {
    // Using Hook to Controle the Input Tags :
    const [enterData, updateData] = useState()

    // Entring Data Control Function :
    // The data is commming as a Object in parameter :
    // To Get data use 'x.target.value' :
    const enteringData = (data) => {
        // Storing the Comming Value  in the Hook's Variable :
        updateData(data.target.value)
    };
    // DataControl for Password :
    const [enterPassword , updatePasswordData] = useState();
    const enteringPasswordData = (data) => {
        // Storing the Comming Value  in the Hook's Variable :
        updatePasswordData(data.target.value)
    };


    // Creating a Function that Shows the Final EnteredData in DOM :
    const [fullData, saveFullData] = useState();
    const showSaveData = () => {
        saveFullData(enterData);
        // It'll print the EnteredPassword in console when the Submit button is pressed  :
        console.log( "The Password = " + enterPassword)
        // OR :
        // console.log(enterData)
    }

    // Preventing the page reloding while Submitting the Form :
    const onFormSubmit = (event)=> {
        event.preventDefault();
    }

    // Redering :
    return (
        <>
            <h2>Hi, {fullData} </h2>
            <form id="form2" onSubmit={onFormSubmit}>
                <input
                    type="text"
                    placeholder="Enter your Name"
                    onChange={enteringData}
                />
                <input
                    type="password"
                    placeholder="Enter your Password"
                    onChange={enteringPasswordData}
                />
                <input
                    type ="submit"
                    value = "Submit/Show"
                    onClick = {showSaveData}
                />
            </form>
        </>
    )
};


// Exporting Component :
export default Form;
