//                  The Single Simple Data Example of Context API                    \\
import React from 'react';
import { useContext } from 'react';

// Importing Context API :
import { FirstName } from './App';



// The Way to Get Single Data From the Context :
const CompB = () => {
    // Creating Context State :
    const FName = useContext(FirstName);
    return (
        <>
            <FirstName.Consumer>
                {
                    (data) => {
                        return (<>
                            <div className="div">
                                The Value that is sent from the Main Component
                            with the Help of Context API without Hooks.</div>
                            <div className="div2">
                                My Name is = {data} , </div>
                        </>)
                    }
                }
            </FirstName.Consumer>
        </>
    )
}


// Exporting CompB :
export default CompB;
