// Importing Modules ;
import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';

const CompA = ()=>{

    // Creating State :
    const [num , updateNum] = useState(0)

    // Creating the UseEffect Hook :
        //*     CASE 1 : By default     *\\
    useEffect( ()=>{
        alert ('The Button is Clicked')
    } )
        //*     CASE 2 : Empty Square Breackets '[]' at the End of the Function     *\\
    useEffect( ()=>{
        alert ('The Button is Clicked')
    } , [])     // Now by potting the Empty Square bracket , the UseEffect hook only run one time when the page load.
        //*     CASE 3 : Square Breackets having a variable '[x]' at the End of the Function     *\\
    useEffect( ()=>{
        alert ('The Button is Clicked')
    } , [num])     // Now by potting the given Variable in the Square bracket , the UseEffect hook only run when the Given variable's Value changed (That leads to update DOM).

    return(
        <>
            <div className = "div1">
                When Ever the Butten is Clicked the UseEffect Hook is Run, as when we press the Butten The Value is changed . 
            <button className = "btn" onClick = { ()=>{ updateNum(num + 1) } }> Click Me {num} </button>
            </div>
        </>
    )
}


// Exporting CompA :
export default CompA;
