//                  ABOUT PAGE AS A COMPONENT                   \\

// Importing Modules :
import React from 'react';
import { useParams } from 'react-router-dom';

const About = (props)=>{
        // The way to initilizing Params :
            // The 'name' variable store the userGiven data by parameters : 
    const {name} = useParams();

    return(
        <>
            <h1> {props.data} {props.loc}  {props.his} </h1>
            This is ABOUT {name} Page.
        </>
    )
}

// Exporting About Component :
export default About;
