//                  COMPILING ALL THE COMPONENTS IN THIS APP COMPONENT                  \\

// Importing Requried Modules :
import React from 'react';

// Importing Components :
import Prs from './Profile'

// Importing CSS :
import "./index.css"

// Importing Array That contain Data of Profiles :
import ProfileData from './ProfileData';




// Rendering : 

//                  METHOD NO.1                 \\
// With-Out Array Mapping :
/*
function App() {
    return (
        <>
            <nav id="nav">OUR DEVELOPER TEAM</nav>
            <div id="profileSection">
                <Prs
                    proname={ProfileData[0].proname}
                    proimg={ProfileData[0].proimg}
                    prolink={ProfileData[0].prolink}
                />
                <Prs
                    proname={ProfileData[1].proname}
                    proimg={ProfileData[1].proimg}
                    prolink={ProfileData[1].prolink}
                />
                <Prs
                    proname={ProfileData[2].proname}
                    proimg={ProfileData[2].proimg}
                    prolink={ProfileData[2].prolink}
                />
                <Prs
                    proname={ProfileData[3].proname}
                    proimg={ProfileData[3].proimg}
                    prolink={ProfileData[3].prolink}
                />
                <Prs
                    proname={ProfileData[4].proname}
                    proimg={ProfileData[4].proimg}
                    prolink={ProfileData[4].prolink}
                />
            </div>
            <footer id="footer">&copy; Copyrights   |   All Rights Reserved</footer>
        </>
    )
};
*/


//                  METHOD NO.2                 \\
// Using Array Mapping 1 :
/*
function maparry(ind) {
    return (
        <Prs
            proname={ind.proname}
            proimg={ind.proimg}
            prolink={ind.prolink}
        />
    )
};

function App() {
    return (
        <>
            <nav id="nav">OUR DEVELOPER TEAM</nav>
            <div id="profileSection">
                {ProfileData.map(maparry)}
            </div>
            <footer id="footer">&copy; Copyrights   |   All Rights Reserved</footer>
        </>
    )
};
*/



// Using Array Mapping 2 :
function App() {
    return (
        <>
            <nav id="nav">OUR DEVELOPER TEAM</nav>
            <div id="profileSection">
                {ProfileData.map((ind) => {
                    return (
                        <Prs
                            proname={ind.proname}
                            proimg={ind.proimg}
                            prolink={ind.prolink}
                        />
                    )
                }
                )}
            </div>
            <footer id="footer">&copy; Copyrights   |   All Rights Reserved</footer>
        </>
    )
};


// Exporting the App Component :
export default App;
