//          MINI-PROJECT IN REACT.JS NO.1         \\

/*
        Adding Dynamic String in HTML usin ReactJS :
              * Show Time & Date on the HTML.
              * Show Good Morning when the time is between 4am To 11am. 
              * Show Good Afternoon when the time is between 11pm To 6pm. 
              * Show Good Night when the time is between 6pm To 4am. 
          // The Greeting Color is also Different on every Greet.
*/

    // Importing Required Modules :
import React from 'react';
import ReactDOM from 'react-dom';
import "./index.css"

      // Creating Date & Time :
var date = new Date();
var curdate = date.toLocaleDateString();
var time = date.toLocaleTimeString();
var hours = date.getHours();
var greet = "";
var greetcss = { };

function t (){
  var n = new Date();
  var ti = n.toLocaleTimeString();
  return ti;
}setInterval(t , 1000)


// var t = setInterval(time , 1000)
// console.log(t)

      // Making Greet Conditions :
if (hours >=4 && hours < 12){
  greet = "Good Morning";
  greetcss.color = "green"
}
else if (hours >=12 && hours <18){
  greet = "Good AfterNoon";
  greetcss.color = "brown"
}
else {
  greet = "Good Night";
  greetcss.color = "gray"
}


    // Rendering Greet :    
ReactDOM.render(
  <>  
  <h2 className="greetHeading">Hellow , Sir <span style={greetcss}> {" " + greet} </span></h2>
  </>,
  document.getElementById('root')
);
    // Rendring Date :
ReactDOM.render(
  <>  
  <h3 className="dt">Date =  <span style={{color:"darkmagenta"}}> {" " + curdate} </span></h3>
  </>,
  document.getElementById('root2')
);

    // Rendering Time :
ReactDOM.render(
  <>  
  <h3 className="dt">Time =  <span style={{color:"darkmagenta"}}> {" " + t()} </span></h3>
  </>,
  document.getElementById('root3')
);


