// Importing Modules ;
import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';



const CompA = () => {

    // Creating State :
    const [num, updateNum] = useState(0)
    const [num1, updateNum1] = useState(0)



    // Rendering :
    return (
        <>
            <div className="div1">
                When Ever the Butten is Clicked the UseEffect Hook is Run, as when we press the Butten The Value is changed .
            <button className="btn" onClick={() => { updateNum(num + 1) }}> Click Me {num} </button>
            </div>
            <div className="div2">
                When Ever the Butten is Clicked the UseEffect Hook is Run, as when we press the Butten The Value is changed .
            <button className="btn" onClick={() => { updateNum1(num1 + 1) }}> Click Me {num1} </button>
            </div>
        </>
    )
}


// Exporting CompA :
export default CompA;
