//                  GREET COMPONENT                 \\

/*  
            Greet :
                In Our 1st mimi Project we make a Greet web Page in which it gives different Greet at Different Conditions.
                    We are Going to Make those greet Component .
*/

        // Require Module For This Component :
import React from 'react';


        // Making Function of the Greet to make Greet Component : 
function reet() {

    // Creating Date & Time :
    var date = new Date();
    var hours = date.getHours();
    var greet = "";
    var greetcss = {};

    // Making Greet Conditions :
    if (hours >= 4 && hours < 12) {
        greet = "Good Morning";
        greetcss.color = "green"
    }
    else if (hours >= 12 && hours < 18) {
        greet = "Good AfterNoon";
        greetcss.color = "brown"
    }
    else {
        greet = "Good Night";
        greetcss.color = "gray"
    };

    return (
        <>
            <h2 className="greetHeading">Hellow , Sir <span style={greetcss}> {" " + greet} </span></h2>
        </>
    )
}

        // Export the Component As :
export default reet;
