import { FilledInput } from "@material-ui/core";
import React, { useState } from "react";

const Form = () => {
  const [FullName, useFullName] = useState();

  const InputEvent = (event) => {

    const value = event.target.value;

    useFullName(value);
  };

  const [showData, updateShowData] = useState()

  const onSubmit = (event) => {
    event.preventDefault();
    console.log(showData)
  };
  
  
  const save = () => {
    updateShowData(()=>{
      return(
        FullName
      )
    })

  }


  return (
    <>
      <form onSubmit={onSubmit} >
        <div>
          <h1>
            {" "}
            Hello, {showData}
          </h1>

          {/* <input
            type="text"
            placeholder="Entre your First Name"
            name="fName"
            onChange={InputEvent}

          />
          <input
            type="text"
            placeholder="Entre your Last Name"
            name="lName"
            onChange={InputEvent} */}

          />
          <input
            type="text"
            placeholder="Entre your Last Email"
            name="Email"
            onChange={InputEvent}
            value = {FullName}
            

          />
          <input
            type="submit"
            value="Save"
            onClick={save}

          />
        </div>
      </form>
    </>
  );
};

export default Form;
