//                  CREATING FORM USING STATE AS A OBJECT WITH CONDITIONS             //
/*
            STATE As a Object :
                    In order to make a Form that contain many Input tags , its difficult to handle the Form with the provious method that we studied in the privious Lectures.
                        As it is hard to Manage the Large Code , So use the State as a Object .
                * using State as a Object is very helpfull to make large Form contain many InputTages.
                    For this Purpose us the Same 'oneChange' Function in all Tages & add 'name' Attribute to Distinguish them.
                        AS :
                            <input
                                type="text"
                                placeholder="Enter your Name"
                                name = "fullName"
                                onChange={enteringData}
                            />
                            <input
                                type="text"
                                placeholder="Enter your Password"
                                name = "pass"
                                onChange={enteringData}
                            />

*/

import React from 'react';
import { useState } from 'react';



// Creating Form Component :
const Form = () => {


    // const [enterData, updateEnterData] = useState({
    //     fullName: "",
    //     lastName: "",
    //     Email: "",
    //     pass: ""
    // });

    // const enteringData = (data) => {

    //     const name = data.target.name;
    //     const value = data.target.value;



    //     updateEnterData((pvalue) => {
    //             return (
    //                 {
    //                     ...pvalue,
    //                     [name]: value
    //                 }
    //             )
           
    //     })
    // }


    const [enterData, updateEnterData] = useState({
        fullName: "",
        lastName: "",
        Email: "",
        pass: ""
    });

    const enteringData = (data) => {

        

        const {name, value} = data.target;

        updateEnterData((pvalue) => {
            if (name == "fullName") {
                return (
                    {
                        fullName: value,
                        lastName: pvalue.lastName,
                        Email: pvalue.Email,
                        pass: pvalue.pass
                    }
                )
            }
            else if (name == "lastName") {
                return (
                    {
                        fullName: pvalue.fullName,
                        lastName: value,
                        Email: pvalue.Email,
                        pass: pvalue.pass
                    }
                )
            }
            else if (name == "Email") {
                return (
                    {
                        fullName: pvalue.fullName,
                        lastName: pvalue.lastName,
                        Email: value,
                        pass: pvalue.pass
                    }
                )
            }
            else {
                return (
                    {
                        fullName: pvalue.fullName,
                        lastName: pvalue.lastName,
                        Email: pvalue.Email,
                        pass: value
                    }
                )
            }
        })
    }











    const [fullData, updateFullData] = useState();
    const showSaveData = () => {
        updateFullData(enterData.fullName + " " + enterData.lastName)
        console.log(`The Name = ${enterData.fullName} & the Password is = ${enterData.pass} `)
    }



    const onFormSubmit = (event) => {
        event.preventDefault();
    }
    return (
        <>
            <h1 style={{ color: "green" }} >FORM WITH OBJECT-STATE</h1>
            <h2>Hi, <span style={{ color: "red" }}> {fullData} </span> </h2>
            <form id="form2" onSubmit={onFormSubmit}>
                <input
                    type="text"
                    placeholder="Enter your Name"
                    name="fullName"
                    onChange={enteringData}
                />
                <input
                    type="text"
                    placeholder="Enter your Last Name"
                    name="lastName"
                    onChange={enteringData}
                />
                <input
                    type="email"
                    placeholder="Enter your Email"
                    name="Email"
                    onChange={enteringData}
                />
                <input
                    type="password"
                    placeholder="Enter your Password"
                    name="pass"
                    onChange={enteringData}
                />
                <input
                    type="submit"
                    value="Submit/Show"
                    onClick={showSaveData}
                />
            </form>
        </>
    )
};








