import React, { useState } from "react";

const Form = () => {
  const [FullName, useFullName] = useState({
    fName: "",
    lName: "",
    Email: "",
  });

  const InputEvent = (event) => {

    const value = event.target.value;
    const name = event.target.name;

    useFullName((pValue) => {
      return (
        {
          ...pValue,
          [name]: value
        }
      )
    });
  };

  const [showData, updateShowData] = useState({
    fName: "",
    lName: "",
    Email: "",
  })

  const onSubmit = (event) => {
    event.preventDefault();
    console.log(showData)
    useFullName({ Email: "" })

  };
  
  
  const save = ()=>{
    updateShowData(FullName)
    
  }


  return (
    <>
      <form onSubmit={onSubmit} >
        <div>
          <h1>
            {" "}
            Hello,{showData.fName} {showData.lName} {showData.Email}
          </h1>

          <input
            type="text"
            placeholder="Entre your First Name"
            name="fName"
            onChange={InputEvent}

          />
          <input
            type="text"
            placeholder="Entre your Last Name"
            name="lName"
            onChange={InputEvent}

          />
          <input
            type="text"
            placeholder="Entre your Last Email"
            name="Email"
            onChange={InputEvent}
            value = {FullName.Email}

          />
          <input
            type="submit"
            value="Save"
            onClick = {save}

          />
        </div>
      </form>
    </>
  );
};

export default Form;
