import React from 'react';
import { useState } from 'react';

import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import DeleteIcon from '@material-ui/icons/Delete';


const Keep = () => {
    const [note, updateNote] = useState({
        title : "",
        content : ""
    })

    const enteringNote = (data) => {
        const  { name , value } = data.target;
        
        updateNote( (preVal)=>{
            return(
                {
                    ...preVal,
                    [name] : value
                }
            )
        }
        )
    }

    const [savedNote, updateSavedNote] = useState("")
    const saveNote = () => {
        updateSavedNote(note)
    }
    return (
        <>
            <div id="mainDiv">
                <input
                    type="text"
                    placeholder="Title"
                    name = "title"
                    value = {note.title}
                    onChange = {enteringNote}
                />
                <textarea
                    column=""
                    rows=""
                    placeholder="Write a Note"
                    name = "content"
                    value = {note.content}
                    onChange={enteringNote}
                />

                <Button id="btn" onClick={saveNote}>
                    <AddIcon id="plus" />
                </Button>
            </div>


        </>
    )
}


export default Keep;
