//          CSS IN REACT.JS         \\

/*
        EXTERNAL CSS :
              The use of external CSS in react is just like the same a use in HTML.
                Write the requried CSS in the Seprate '*.css' file for the JSX Element & Encluded it to the React (index.js).
                  The Including method of External CSS is quit different in React as compare to HTML.
                    TO Enclude the External CSS , we have to importe the CSS file in the React.
*/

    // Importing Required Modules :
import React from 'react';
import ReactDOM from 'react-dom';

    // Importing External CSS :
import "./index.css"
        // './' Means the Sane Directory , in JavaScript we have also to write the present directory command.

  var name = "Jarviz";
  var profession = "Artificial Intelligence";
  var img = "https://picsum.photos/200/300"
  var link = "https://facebook.com"

    // Rendering :    
ReactDOM.render(
      // contentEditable Attribute makes the given elemnt EditAble.
  <>  
  <h1 contentEditable="true">This Element is EditAble.</h1>
  <a href="https://google.com">Got TO Google.</a> 
  </>,
  document.getElementById('root')
);


      // Adding Images & use other Attributes : (for Practice)             
ReactDOM.render(
        // In JSX Image Element , it must be Closing tag in JSX as :
          // <img src="#"> </img>---OR---<img src="#" />
  <>
  <h2>In JSX Image Tag Must be in Opening & Closing Tang :</h2>
  <img src="https://picsum.photos/200/300" alt="Random Image"></img>
  <img src="https://picsum.photos/300/300" alt="Random Inage" />  
  </>,
  document.getElementById('root2')
);


        // In JSX the Line break Element also must have closing tag as :
          // <br></br>------OR-------<br/>
ReactDOM.render(
  <>
  <h2>Putting Images/links Address From a Variables :</h2>
  <img src={img} alt="Random Inage" /> <br/>
  <a href={link}>Facobook Link's Address From Variable</a>
  </>,
  document.getElementById('root3')
);


                    //                END                 \\
