// Importing Modules ;
import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';

const CompA = ()=>{

    // Creating State :
    const [num , updateNum] = useState(0)

    useEffect( ()=>{
        alert ('The Button is Clicked')
    })

    return(
        <>
            <div className = "div1">
                When Ever the Butten is Clicked the UseEffect Hook is Run, as when we press the Butten The Value is changed . 
            <button className = "btn" onClick = { ()=>{ updateNum(num + 1) } }> Click Me {num} </button>
            </div>
        </>
    )
}


// Exporting CompA :
export default CompA;
