//              DATE COMPONENT          \\

/*
            Date :
                In Our 1st mimi Project we make a Greet web Page in which it Show Current Date.
                    We are Going to Make those Date as a Component .
*/

        // Required Modules For This Component :
import React from "react";

        // Making Function of the Date to make Date Component :
function CDate() {
    // Creating Date :
    var date = new Date();
    var curdate = date.toLocaleDateString();

    return (
        <>
            <h3 className="dt">Date =  <span style={{ color: "darkmagenta" }}> {" " + curdate} </span></h3>
        </>
    )

};


//              TIME COMPONENT          \\

/*
            TIME :
                In Our 1st mimi Project we make a Greet web Page in which Current Time is Shown.
                    We are Going to Make those Time as a Component .
*/

        // Making Function of the Time to make Time Component :
function Ctime() {
        // Creating Time :
    var date = new Date();
    var time = date.toLocaleTimeString();

    return (
        <>
            <h3 className="dt">Time =  <span style={{ color: "darkmagenta" }}> {" " + time} </span></h3>
        </>
    )

};

        // Export the Component As :
export default Ctime + {CDate};

