//                  ABOUT PAGE AS A COMPONENT                   \\

// Importing Modules :
import React from 'react';
import { useHistory, useLocation, useParams } from 'react-router-dom';

const About = (props) => {
    // The way to initilizing Params :
    // The 'name' variable store the userGiven data by parameters : 
    const { name, id } = useParams();

    // The way to initializing Location :
    const Location = useLocation();
    console.log(Location)

    // The way to initializing History :
    const history = useHistory();
    console.log(history)

        // Rendering:
    return (
        <>
            <h5> {props.data} {props.loc}  {props.his} </h5> <br />
            This is ABOUT {name} {id} Page.
            <h6>The Location of the Page is = " {Location.pathname} " </h6>
            {Location.pathname == "/about/Junaid/Latif" ? <button id="sbtn" onClick={() => alert("Warning This is Secret")}> Admnistration Login </button> : null}
            <button id="sbtn" onClick={() => history.goBack()}> Back </button>
            <button id="sbtn" onClick={() => history.push('/')}> Home </button>

        </>
    )
}

// Exporting About Component :
export default About;
