//                  The Multiple Data Example of Context API                    \\
import React from 'react';
import { useContext } from 'react';

// Importing Context API :
import { Name, AI } from './App';


// The Way to Get Multiple Data From Context :
const ComplexCompB = () => {

    // Creating Context State :
    const JName = useContext(Name);
    const AName = useContext(AI);

    // Rendering :
    return (
        <>

            <>
                My Assistant is {JName} & is a PowerFull {AName}
            </>

        </>
    )
}


// Expotrting the ComplexCompB :
export default ComplexCompB;
