import React, { useState } from "react";

const Form = () => {
  const [FullName, useFullName] = useState({
    fName: "",
    lName: "",
    Email: "",
  });

  const InputEvent = (event) => {
    console.log(event.target.value);

    const value = event.target.value;
    const name = event.target.name;

    useFullName((pValue) => {
      if (name == "fName") {
        return {
          fName: value,
          lName: pValue.lName,
          Email:pValue.Email,
        };
      } else if (name == "lname") {
        return {
          fName: pValue.fName,
          lName: value,
          Email:pValue.Email,
        };
      } else if (name == "email") {
        return {
          fName: pValue.fName,
          lName: pValue.lName,
          Email:value,

        };
      }
    });
  };

  const onSubmit = (event) => {
    event.preventDefault();
  };
  return (
    <>
      <form onSubmit={onSubmit}>
        <div>
          <h1>
            {" "}
            Hello,{FullName.fName} {FullName.lName} {FullName.Email}{" "}
          </h1>

          <input
            type="text"
            placeholder="Entre your First Name"
            name="fName"
            onChange={InputEvent}
            value={FullName.fName}
          />
          <input
            type="text"
            placeholder="Entre your Last Name"
            name="lName"
            onChange={InputEvent}
            value={FullName.lName}
          />
          <input
            type="text"
            placeholder="Entre your Last Email"
            name="Email"
            onChange={InputEvent}
            value={FullName.Email}
          />
          <button type="submit"> submit</button>
        </div>
      </form>
    </>
  );
};

export default Form;
