// Importing Requried Modules :
import React from 'react';
import { createContext } from 'react';

import './index.css'
// Importing Components :
import CompA from './CompA'

import ComplexCompA from './ComplexCompA'

// Creating the Context API :
    // Single Data Way :
const FirstName = createContext();
// Multiple Data Way :
const Name = createContext();
const AI = createContext();

const App = ()=>{
    return (
        <>
            {/* This is the Way to Send Single Data */}
        <FirstName.Provider value = {"JUNAID"}>
            <CompA/>
        </FirstName.Provider>

                {/* This is the Way to Send MultiData  */}
        <div className="div1">
        <Name.Provider value = {"JARVIZ"}>
            <AI.Provider value = {"Artificial Intelligence"}>
                <ComplexCompA/>
            </AI.Provider>
        </Name.Provider>
        </div></>
    )
}

export default App;
// Also Export the Context API :
export {FirstName};
export {Name , AI};
