//                  CERATING LIVE TIME APP WITH USE-STATE HOOK IN DOM                  \\


// Importing Requried Modules :
import React from 'react';
// Importing Hooks's Hodule (UseState) :
// This Module is Automatic Imported when you use Hook(UseState) in the Project :
import { useState } from 'react';

// Imorting CSS :
import './index.css'


// Creating Date :
var date = new Date();
var curdate = date.toLocaleDateString();


// Making Greet Conditions :
var greet = "";
var greetcss = {};
var hours = date.getHours();

if (hours >= 4 && hours < 12) {
    greet = "Good Morning";
    greetcss.color = "green"
}
else if (hours >= 12 && hours < 18) {
    greet = "Good AfterNoon";
    greetcss.color = "brown"
}
else {
    greet = "Good Night";
    greetcss.color = "gray"
}

// Creating the App Component :
const App = () => {
        // Creating Time :
            // As the Hooks is code with in the Man Function , So it'll be code here :
    let time = new Date().toLocaleTimeString();
    const [CurTime , LiveTime] = useState(time);

        // Making Time Live As :
    const UpdateTime =  ()=>{
        LiveTime(time);
        console.log(CurTime)
    };
    setInterval(UpdateTime , 1000);
    

    // Rendring :
    return (
        <>
            <h2 className="greetHeading">Hellow , Sir <span style={greetcss}> {" " + greet} </span></h2>
            <h3 className="dt">Date =  <span style={{ color: "darkmagenta" }}> {" " + curdate} </span></h3>
            <h3 className="dt">Time =  <span style={{ color: "darkmagenta" }}> {CurTime} </span></h3>
        </>
    )
};


// Exporting App Component :
export default App;
