//                  CREATING FORM WITHOUT 'FORM' TAG JUST BY INPUT TAGS             //
/*
            In React the Input Tag's Data Can't be access by the Attributes called 'name' ,
             So we can use Attributes like 'onChange' to track the real time change in the Input Tag & manuplate it,
              with the Help of Hooks(useState).
              
*/
// Importing Requried Modules :
import React from 'react';
import { useState } from 'react';



// Creating Form Component :
const Form = () => {
    // Using Hook to Controle the Input Tags :
    const [enterData , updateData] = useState()

    // Entring Data Control Function :
        // The data is commming as a Object in parameter :
            // To Get data use 'x.target.value' :
    const enteringData = (data)=> {
        // Storing the Comming Value  in the Hook's Variable :
        updateData(data.target.value)
            // It'll print the EnteringData in console everyTime wen the Key is pressed  :
        console.log(enterData)

    };


    // Creating a Function that Shows the Final EnteredData in DOM :
    const [fullData , saveFullData] = useState();
    const showSaveData = ()=> {
        saveFullData(enterData);
    }
    

    // Redering :
    return (
        <>
            <h1>Hellow, {fullData} </h1>
            <input
                type="text"
                placeholder="Enter your Name"
                value = {enterData}
                onChange = {enteringData}
            />
            <button
                onClick={showSaveData}
            > Save/Show</button>
        </>
    )
};


// Exporting Component :
export default Form;
