import React from 'react';
import { useState } from 'react';

import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import DeleteIcon from '@material-ui/icons/Delete';


const Keep = () => {
    const [note, updateNote] = useState("")

    const enteringNote = (data) => {
        updateNote(data.target.value)
    }

    const [savedNote , updateSavedNote] = useState("")
    const saveNote = ()=>{
        updateSavedNote(note)
    }
    return (
        <>
            <div id="mainDiv">
                <input
                    type="text"
                    placeholder="Title"
                    />
                <textarea
                    column=""
                    rows=""
                    placeholder="Write a Note"
                    onChange={enteringNote}
                />

                <Button id="btn" onClick={saveNote}>
                    <AddIcon id="plus" />
                </Button>
            </div>

            <div id="noteDiv">
                {savedNote}
                <Button>
                    <DeleteIcon />
                </Button>
            </div>
            <h1> This Keeper App {note} </h1>
        </>
    )
}


export default Keep;
