//                  CREATING TO-DO APP                  \\



// Importing Required Modules :
import React from 'react';
import { useState } from 'react';

// Importing Material UI Icons :
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';



const Todo = () => {
    const [note, updateNote] = useState("Junaid")

    const addingNote = (data) => {
        updateNote(data.target.value)
    }

    const [savedNote, savingNote] = useState([])
    const saveNote = () => {
        savingNote(note)
    }

    return (
        <>
            <div id="AppDiv">
                <h1>TO DO LIST</h1>
                <input
                    type="text"
                    placeholder="Add Item"
                    onChange={addingNote}

                />
                <Button id="btn" variant="contained" onClick={saveNote}> <AddIcon /> </Button>
                <div id="ListDiv">
                    <ol>
                    <list>{savedNote}</list>
                    </ol>
                </div>
            </div>
        </>
    )
}


// Exporting Form Component :
export default Todo;
