//                  CREATING COMLETE FORM USING STATE AS A OBJECT & SPREED OPERATOR                  \\
/*
            DEFINE SPREED ('...') OPERATOR :
                        The spread operator is a useful and quick syntax for adding items to arrays,
                         combining arrays or objects, and spreading an array out into a function's arguments.
                    AS :
                        const frontEnd = ['Junaid' , 'Zahid' , 'Zulifqar]; 
                        const backEnd = ['Tayyab' , 'Shahzaib']; 
                            *** Concatinating both Arrays into one Array :
                                    const All = [...frontEnd , ...backEnd];
                        *** The Same Above Method is also applicable to Objects.
*/


    // Importing Required Modules :
import React from 'react';
import { useState } from 'react';


    // Creating Form Component :
const Form = ()=>{
        // Creating Hook to Control the InputTag's values :
    const [enteredData , updatingData] = useState({
        fullName : "",
        email : "",
        pass : "",
        age : "",
        phone : ""
    })


        // Creating Data Control Function :
    const enteringData = (data)=>{
            // Creating Variables of Required Status of name Attribute & its value
        // const value =  data.target.value;
        // const name = data.target.name;
            //**  Alternative With the help of Object Distructuring :
        const {value , name} = data.target

            // updating EnteredData in the State Using Spreed Operator;
        updatingData((previousValue)=>{
            return{
                    // This will Concatenate the Values that is Present PreviousValue (a Object) for Every InputTag,
                        // Also it'll update the Value of Every name's Attribute.
                ...previousValue,
                    // [name] means give the value of name from the Object not the Key.
                [name] : value
            }
        })
    };


        // Creating a Function that Shows the Final EnteredData in DOM :
    const [fullData, saveFullData] = useState();
    const showSaveData = () => {
        saveFullData(enteredData.fullName);
            // It'll print the EnteredPassword in console when the Submit button is pressed  :
        console.log( "The Email = " + enteredData.email)
        console.log( "The Password = " + enteredData.pass)
        console.log( "The Age = " + enteredData.age)
        console.log( "The Phone = " + enteredData.phone)
            // OR :
        // console.log(enterData)

            // Creating Allert to Conformation of Submition :
        alert('Form Submitted')
    }


        // Preventing the page reloding while Submitting the Form :
    const onFormSubmit = (event)=> {
        event.preventDefault();
    }


    // Rendering :
    return (
        <>
            <h1 style = { {color : "green"} } >COMPLETE FORM</h1>
            <h2>Hi, <span style = { {color : "red"} }> {fullData} </span> </h2>
            <form id="form2" onSubmit={onFormSubmit}>
                <input
                    type="text"
                    placeholder="Enter your Name"
                    name = "fullName"
                    onChange={enteringData}
                />
                <input
                    type="email"
                    placeholder="Enter your Email"
                    name = "email"
                    onChange={enteringData}
                />
                <input
                    type="password"
                    placeholder="Enter your Password"
                    name = "pass"
                    onChange={enteringData}
                />
                <input
                    type="number"
                    placeholder="Enter your Age"
                    name = "age"
                    onChange={enteringData}
                />
                <input
                    type="number"
                    placeholder="Enter your Mobile No."
                    name = "phone"
                    onChange={enteringData}
                />


                <input
                    type ="submit"
                    value = "Submit/Show"
                    onClick = {showSaveData}
                />
            </form>
        </>
    )
};


    // Exporting Form Component :
export default Form;
