// Importing Requried Modules :
import React from 'react';
import { Route, Switch } from 'react-router-dom'

// Importing Requried Components :
import Navbar from './Navbar'
import Home from './Home'
import Services from './Services'
import About from './About'
import Contact from './Contact'
import Error from './Error'
import Foot from './Footer'

// Importing CSS :
import './index.css'

const App = () => {

    // Rendering :
    return (
        <>
            <Navbar />
            <div className="box">
                    {/* The way to handle the Routes  */}
                    {/* Exect property is use to prevent the partial matching url address */}
                <Switch>
                    <Route exact path="/" component={Home} />
                    <Route path="/services" component={Services} />
                    <Route path="/about" component={About} />
                    <Route path="/contact" component={Contact} />
                    <Route component={Error} />
                </Switch>
            </div>
            <Foot/>
        </>
    )
}


            //          DIFFERENCE BETWEEN REACT ROUTE COMPONENT VS RENDER          \\
/*
                        When you use component (instead of render) the router create a new React element from the given component.
                         That means if you provide an inline function to the componentprop, you would create a new component every render.
                          This results in the existing component unmounting and the new component mounting instead of just updating the existing component.
                           When using an inline function for inline rendering, use the RENDER because it just update the change in the page not the whole page. .
                        * There is not performance different between component and render prop if you are use component={AppComponent} directly,
                           if you want to assign some props to AppComponent, use render={() => <AppComponent {...props}/> } instead of component={() => <AppComponent {...props}/> }
*/



