//          JSX EXPRESSION IN REACT         \\

/*
      Rendering Variable OR Expressions in the JSX :
            To rander a Variable/Expression in the JSX Element we use the Curly Brackets '{}'.
              Put the Variable/Expression in the Curly Brackets '{}' within the JSX Element to Render it. 
*/

    // Importing Required Modules :
import React from 'react';
import ReactDOM from 'react-dom';



    // Rendering :
      // Add Variable/Expression without Curly '{}' Brackets
ReactDOM.render(
  <h1>Hellow this is Added by JSX.</h1> ,
  document.getElementById('root')
);
ReactDOM.render(
  <h1>Hellow this is Added by JSX.</h1> ,
  document.getElementById('root')
);

ReactDOM.render(
  <div>
  <h1>Hellow this Multiple Elements Added by JSX with HTML Element.</h1>
  <p>This is the Second Element.</p> 
  </div>,
  document.getElementById('root')
);



ReactDOM.render(
  <React.Fragment>
  <h1>Hellow this Multiple Elementes Added by JSX with Fragment .</h1>
  <p>This is the Second Element.</p> 
  </React.Fragment>,
  document.getElementById('root2')
);
// ---------------OR---------------
ReactDOM.render(
  <>
  <h1>Hellow this Multiple Elementes Added by JSX with Fragment .</h1>
  <p>This is the Second Element.</p> 
  </>,
  document.getElementById('root2')
);

    // Using Array ,
      // It also behaive as a Fragment Element :
ReactDOM.render(
  [
  <h1>Hellow this Multiple Elementes Added by JSX with ARRAY .</h1> , // Every Elements must be Seperated by comma in Array.
  <p>This is the Second Element.</p> 
  ],
  document.getElementById('root3')
);    
          // This Array Metod is Added in React after React 16+ Versions.



          //        END       \\
