//                  CREATING THE CONDITIONS IN THIS COMPONENT                  \\

// Importing Requried Modules :
import React from 'react';

// Importing Components :
import Full from './FullStack';
import Front from './FrontEnd';
import Back from './BackEnd';

// Importing CSS :
import "./index.css"

const con = prompt("Please Enter Your Requried List Name : ", "'full' , 'front' , 'back'")

//                  USING IF-ELSE                   \\
/*
const Conditions = () => {
    if (con == "full") {
        return (
            <>
                <Full />
            </>
        )
    }
    else if (con == "front") {
        return (
            <>
                <Front />
            </>
        )
    }
    else if (con == "back") {
        return (
            <>
                <Back />
            </>
        )
    }
    else {
        return (
            <>
                <h1>404 ERROR</h1>
                <h4>Please Enter the Correct Word as Sugested , Also make sure that the Spelling is correct.</h4>
            </>
        )
    }
};
*/



//                  USING TERNARY OPERATOR                  \\

const Conditions = () => {
    return (
        (con == "full") ? <Full /> : (con == "front") ? <Front /> : (con == "back") ? <Back /> :
            <>
                <h1>404 ERROR</h1>
                <h4>Please Enter the Correct Word as Sugested , Also make sure that the Spelling is correct.</h4>

            </>
    )
};


function App() {
    return (
        <>
            <nav id="nav">OUR DEVELOPER TEAM</nav>
            <div id="profileSection">
                {/* {(con == "full") ? <Full /> : (con == "front") ? <Front /> : (con == "back") ? <Back /> : <> <h1>404 ERROR</h1> </>} */}
                <Conditions />
            </div>
            <footer id="footer">&copy; Copyrights   |   All Rights Reserved</footer>
        </>
    )
};


// Exporting the App Component :
export default App;
