//                  CREATING TO-DO APP                  \\



// Importing Required Modules :
import React from 'react';
import { useState } from 'react';

// Importing Material UI Icons :
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';



const Todo = () => {
    const [note, updateNote] = useState("Junaid")

    const addingNote = (data) => {
            updateNote(data.target.value)
    }

    const [savedNote , savingNote] = useState("")
    const saveNote = ()=>{
        savingNote(note)
    }

    return (
        <>
            <div id="AppDiv">
                <h1>Add Node</h1>
                <input
                    type="text"
                    placeholder="Add Something"
                    onChange={addingNote}

                />
                <Button id="btn" variant="contained" onClick={saveNote}> <AddIcon /> </Button>
                <div id="ListDiv">
                    <list>{savedNote}</list>
                </div>
            </div>
        </>
    )
}


// Exporting Form Component :
export default Todo;
