//              COMPONENT FOR PROFILE CARD                  //

    // Importing Require Module :
import React from 'react';

    // Creating the Component Named 'Pro' :
function Pro(data) {

    // Returning the JSX :
    return (
        <>
            <div id="ProfileCard">
                <div id="ProfileImg">
                    <img src={data.proimg} alt="ERROR"></img>
                </div>
                <div id="ProfileContent">
                    <strong>NAME : </strong> <span>{data.proname}</span> <br />
                    <strong>PROFESSION : </strong> <span>Software Developer</span> <br/>
                    <strong>EXPERTISE :</strong> <span> {data.proexpert} </span>
                    <button href={data.prolink}>Profile</button>

                </div>
            </div>
        </>
    )
};

    // Exporting the Component :
export default Pro;
