//          MINI-PROJECT IN REACT.JS NO.1         \\

/*
        Adding Dynamic String in HTML usin ReactJS :
              * Show Time & Date on the HTML.
              * Show Good Morning when the time is between 4am To 11am. 
              * Show Good Afternoon when the time is between 11pm To 6pm. 
              * Show Good Night when the time is between 6pm To 4am. 
          // The Greeting Color is also Different on every Greet.
*/

    // Importing Required Modules :
import React from 'react';
import ReactDOM from 'react-dom';
import "./index.css"

      // Creating Date :
var date = new Date();
// var curdate = date.toLocaleDateString();
var day = date.getDay();
var hours = date.getHours();
var greet = "";
var greetcss = { };

// console.log(curdate)
console.log(day)

      // Making Greet Conditions :
if (hours >=4 && hours < 12){
  greet = "Good Morning";
  greetcss.color = "green"
  console.log("morning")
}
else if (hours >=12 && hours <18){
  greet = "Good AfterNoon";
  greetcss.color = "brown"
  console.log("NOON")
}
else {
  greet = "Good Night";
  greetcss.color = "gray"
  console.log("night")
}


    // Rendering :    
ReactDOM.render(
  <>  
  <h2 className="greetHeading">Hellow , Sir <span style={greetcss}> {" " + greet} </span></h2>
  </>,
  document.getElementById('root')
);
