import React from 'react';

// Importing Context API :
import { FirstName } from './App';

const CompB = () => {
    return (
        <>
            <FirstName.Consumer>
                {
                    (data) => {
                        return (<> The Value that is sent from the Main Component with the Help of Context API without Hooks is = {data} </>)
                    }
                }
            </FirstName.Consumer>
        </>
    )
}

export default CompB;
