//              TIME cOMPONENT          \\

/*
            TIME :
                In Our 1st mimi Project we make a Greet web Page in which Current Time is Shown.
                    We are Going to Make those Time as a Component .
*/

        // Require Module For This Component :
import React from 'react';

        // Making Function of the Time to make Time Component :
function Ctime() {
        // Creating Time :

    var date = new Date();
    var time = date.toLocaleTimeString();

        // Making Time Live :

    setInterval(time , 1000)

    return (
        <>
            <h3 className="dt">Time =  <span style={{ color: "darkmagenta" }}> {" " + time} </span></h3>
        </>
    )

};

        // Export the Component As :
export default Ctime;
