//                  CREATING FORM USING STATE AS A OBJECT WITH CONDITIONS             //
/*
            STATE As a Object :
                    In order to make a Form that contain many Input tags , its difficult to handle the Form with the provious method that we studied in the privious Lectures.
                        As it is hard to Manage the Large Code , So use the State as a Object .
                * using State as a Object is very helpfull to make large Form contain many InputTages.
                    For this Purpose us the Same 'oneChange' Function in all Tages & add 'name' Attribute to Distinguish them.
                        AS :
                            <input
                                type="text"
                                placeholder="Enter your Name"
                                name = "fullName"
                                onChange={enteringData}
                            />
                            <input
                                type="text"
                                placeholder="Enter your Password"
                                name = "pass"
                                onChange={enteringData}
                            />

*/

import React from 'react';
import { useState } from 'react';



    // Creating Form Component :
const Form = () => {
        // Using Hook to Controle the Input Tags :
            // Creating State As a Object
    const [enterData, updateData] = useState({
            // This the values of a name Attribute present in the InputTages.
        fullName : "",
        pass : ""
    })
        // Creating Data Control Function :
    const enteringData = (data) => {
            // This will print the Current Accessing InputTag
        console.log(data.target.name)

            // The variable that Holds the Changed Value from the Given Tag.
        const value = data.target.value;
            // The variable that Holds the Status of current Accessing Tag. 
        const name = data.target.name;
        
            // Storing the Comming Value  in the Hook's Object's Variable :
                // This 'updateData' have a Function's Parameter that containg the Previous Stored Value of the Respective Object's Variables.
        updateData ( (previousValue)=>{
                // If the InputTag that's Name Attribute value is 'fullName' , than this Code is Executed:
            if (name == "fullName"){
                return {
                    fullName : value,
                    pass : previousValue.pass
                };
            }
                // If the InputTag that's Name Attribute value is 'pass' , than this Code is Executed:
            else if (name == "pass"){
                return{
                    fullName : previousValue.fullName,
                    pass : value
                };
            }
        });
    };


        // Creating a Function that Shows the Final EnteredData in DOM :
    const [fullData, saveFullData] = useState();
    const showSaveData = () => {
        saveFullData(enterData.fullName);
            // It'll print the EnteredPassword in console when the Submit button is pressed  :
        console.log( "The Password = " + enterData.pass)
            // OR :
        // console.log(enterData)

            // Creating Allert to Conformation of Submition :
        alert('Form Submitted')
    }


        // Preventing the page reloding while Submitting the Form :
    const onFormSubmit = (event)=> {
        event.preventDefault();
    }


        // Redering :
    return (
        <>
            <h1 style = { {color : "green"} } >FORM WITH OBJECT-STATE</h1>
            <h2>Hi, <span style = { {color : "red"} }> {fullData} </span> </h2>
            <form id="form2" onSubmit={onFormSubmit}>
                <input
                    type="text"
                    placeholder="Enter your Name"
                    name = "fullName"
                    onChange={enteringData}
                />
                <input
                    type="password"
                    placeholder="Enter your Password"
                    name = "pass"
                    onChange={enteringData}
                />
                <input
                    type ="submit"
                    value = "Submit/Show"
                    onClick = {showSaveData}
                />
            </form>
        </>
    )
};



// Exporting Component :
export default Form;
