//                  CERATING NUMBER INCREMENTED APP IN DOM                  \\

// Importing Requried Modules :
import React from 'react';
import { useState } from 'react';

import './index.css'



const App = () => {

    const [oldnumber, update] = useState(0);

    const incriment = () => {
        update(oldnumber + 1);
    };

    return (
        <>
            <h1>Click the Button To Incriment The Number :</h1>
            <h3> {oldnumber} </h3>
            <button onClick={incriment}> Click TO Incriment. </button>
        </>
    )
};

export default App;
