//          ADDING MULTIPLE ELEMENTS IN JSX         \\


    // Importing Required Modules :
import React from 'react';
import ReactDOM from 'react-dom';


    // Rendering :
      // We can only add a Single JSX while rendering , AS :
ReactDOM.render(
  <h1>Hellow this is Added by JSX.</h1> ,
  document.getElementById('root')
);
ReactDOM.render(
  <h1>Hellow this is Added by JSX.</h1> ,
  document.getElementById('root')
);
      // If we add more Elements as :
          /*  ReactDOM.render(
                <h1>Hellow this is Added by JSX.</h1> ,
                <p>This is the Second Element</p>,
                document.getElementById('root')
              );
          */ 
              // It'll gives error.

    // In Case of adding Multiple Element , 
            // AS :
ReactDOM.render(
  <div>
  <h1>Hellow this is Added by JSX.</h1>
  <p>This is the Second Element.</p> 
  </div>,
  document.getElementById('root')
);
      // This is the way , If we add multiple element , enclosed them into the single Element,
        // It seams that rander having a Single JSX , no maters what it have .
