//                  CREATING COMLETE FORM USING STATE AS A OBJECT                   \\


    // Importing Required Modules :
import React from 'react';
import { useState } from 'react';


    // Creating Form Component :
const Form = ()=>{
        // Creating Hook to Control the InputTag's values :
    const [enteredData , updatingData] = useState({
        fullName : "",
        email : "",
        pass : "",
        age : "",
        phone : ""
    })


        // Creating Data Control Function :
    const enteringData = (data)=>{
            // Creating Variables of Required Status of name Attribute & its value
        // const value =  data.target.value;
        // const name = data.target.name;
            //**  Alternative With the help of Object Distructuring :
        const {value , name} = data.target

            // updating EnteredData in the State Using Conditions for every InputTages ;
        updatingData((previousValue)=>{
            if (name == "fullName"){
                return{
                    fullName :value,
                    email :previousValue.email,
                    pass :previousValue.pass,
                    age :previousValue.age,
                    phone :previousValue.phone
                };
            }
            else if (name == "email"){
                return{
                    fullName :previousValue.fullName,
                    email :value,
                    pass :previousValue.pass,
                    age :previousValue.age,
                    phone :previousValue.phone
                };
            }
            else if (name == "pass"){
                return{
                    fullName :previousValue.fullName,
                    email :previousValue.email,
                    pass :value,
                    age :previousValue.age,
                    phone :previousValue.phone
                };
            }
            else if (name == "age"){
                return{
                    fullName :previousValue.fullName,
                    email :previousValue.email,
                    pass :previousValue.pass,
                    age :value,
                    phone :previousValue.phone
                };
            }
            else if (name == "age"){
                return{
                    fullName :previousValue.fullName,
                    email :previousValue.email,
                    pass :previousValue.pass,
                    age :previousValue.age,
                    phone :value
                };
            }
        })
    };


    // Rendering :
    return (
        <>
            <h1 style = { {color : "green"} } >COMPLETE FORM</h1>
            <h2>Hi, <span style = { {color : "red"} }> {fullData} </span> </h2>
            <form id="form2" onSubmit={onFormSubmit}>
                <input
                    type="text"
                    placeholder="Enter your Name"
                    name = "fullName"
                    onChange={enteringData}
                />
                <input
                    type="email"
                    placeholder="Enter your Email"
                    name = "email"
                    onChange={enteringData}
                />
                <input
                    type="password"
                    placeholder="Enter your Password"
                    name = "pass"
                    onChange={enteringData}
                />
                <input
                    type="number"
                    placeholder="Enter your Age"
                    name = "age"
                    onChange={enteringData}
                />
                <input
                    type="number"
                    placeholder="Enter your Mobile No."
                    name = "phone"
                    onChange={enteringData}
                />


                <input
                    type ="submit"
                    value = "Submit/Show"
                    onClick = {showSaveData}
                />
            </form>
        </>
    )
};
