/*
            Date :
                In Our 1st mimi Project we make a Greet web Page in which it Show Current Date.
                    We are Going to Make those Date Component .
*/

// Required Modules For This Component :
import React from "react";
function Date() {
    // Creating Date :
    var date = new Date();
    var curdate = date.toLocaleDateString();
    var time = date.toLocaleTimeString();

    return (
        <>
            <h3 className="dt">Date =  <span style={{ color: "darkmagenta" }}> {" " + curdate} </span></h3>
        </>
    )

};

export default Date;
